import React, { useState } from 'react';
import axios from 'axios'; 
import 'bootstrap/dist/css/bootstrap.min.css';
import'../App.css';
import { useNavigate } from 'react-router-dom';



function Dashboard() {


    const [activeTab, setActiveTab] = useState('patientInfo');

  const renderContent = () => {
    switch (activeTab) {
      case 'patientInfo':
        return (
          <div>
            <h2>Patient Information</h2>
            {/* Add your patient information content here */}
          </div>
        );
      case 'medicalRecords':
        return (
          <div>
            <h2>Medical Records</h2>
            {/* Add your medical records content here */}
          </div>
        );
      case 'appointmentDetails':
        return (
          <div>
            <h2>Appointment Details</h2>
            {/* Add your appointment details content here */}
          </div>
        );
      case 'userDetails':
        return (
          <div>
            <h2>User Details</h2>
            {/* Add your user details content here */}
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="dashboard">
      {/* Navigation Menu */}
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <div className="container">
          <a className="navbar-brand" href="/dashboard">Dashboard</a>
          <button
            className="navbar-toggler"
            type="button"
            data-toggle="collapse"
            data-target="#navbarNav"
            aria-controls="navbarNav"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav ml-auto">
              <li className="nav-item">
                <a className="nav-link" href="/dashboard/profile">
                  Profile
                </a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="/dashboard/settings">
                  Settings
                </a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="/dashboard/logout">
                  Logout
                </a>
              </li>
            </ul>
          </div>
        </div>
      </nav>
      <div className="container mt-4">
        <h1>Welcome to Your Dashboard</h1>

        {/* Buttons to switch between content */}
        <div className="mb-4">
          <button
            className={`btn btn-primary mr-2 ${
              activeTab === 'patientInfo' ? 'active' : ''
            }`}
            onClick={() => setActiveTab('patientInfo')}
          >
            Patient Info
          </button>
          <button
            className={`btn btn-primary mr-2 ${
              activeTab === 'medicalRecords' ? 'active' : ''
            }`}
            onClick={() => setActiveTab('medicalRecords')}
          >
            Medical Records
          </button>
          <button
            className={`btn btn-primary mr-2 ${
              activeTab === 'appointmentDetails' ? 'active' : ''
            }`}
            onClick={() => setActiveTab('appointmentDetails')}
          >
            Appointment Details
          </button>
          <button
            className={`btn btn-primary ${
              activeTab === 'userDetails' ? 'active' : ''
            }`}
            onClick={() => setActiveTab('userDetails')}
          >
            User Details
          </button>
        </div>

        {/* Render the content based on the selected tab */}
        {renderContent()}
      </div>
    </div>
  );






}

export default Dashboard;
